import React, { useState } from "react";
import axios from "axios";
import swal from "sweetalert";
import { Link } from "react-router-dom";
export default function ViewCategory() {
  // initial value
  const [categoryInput, setCategoryInput] = useState({
    slug: "",
    name: "",
    des: "",
    status: "",
    meta_title: "",
    meta_keyword: "",
    meta_des: "",
    errors: [],
  });

  // setting value
  const handleInput = (e) => {
    setCategoryInput({ ...categoryInput, [e.target.name]: e.target.value });
  };

  // handle submit
  const categorySubmit = (e) => {
    e.preventDefault();

    let data = {
      slug: categoryInput.slug,
      name: categoryInput.name,
      des: categoryInput.des,
      status: categoryInput.status,
      meta_title: categoryInput.meta_title,
      meta_keyword: categoryInput.meta_keyword,
      meta_des: categoryInput.meta_des,
    };

    axios.post(`api/store-category`, data).then((res) => {
      console.log(res);
      if (res.data.status == 200) {
        swal({
          title: "Success",
          text: res.data.message,
          icon: "success",
        });

        document.getElementById("CATEGORY_FORM").reset();
      }
      if (res.data.status == 400) {
        setCategoryInput({ ...categoryInput, errors: res.data.errors });
      }
    });
  };

  // display error
  let display_error = [];

  if (categoryInput.errors) {
    display_error = [
      categoryInput.errors.slug,
      categoryInput.errors.name,
      categoryInput.errors.meta_title,
    ];
  }
  return (
    <>
      <div className="container px-4">
        <div className="card mt-4">
          <div className="card-header d-flex justify-content-between align-items-center">
            <h2 className="my-4">Category List</h2>
            <Link
              to="/admin/add-category"
              className="btn btn-primary btn-sm px-4 text-capitalize"
            >
              add category
            </Link>
          </div>
          <div className="card-body"></div>
        </div>
      </div>
    </>
  );
}
