import axios from "axios";
import swal from "sweetalert";
import React, { useEffect, useState } from "react";
import { Link, useHistory } from "react-router-dom";
import { BASE_URL } from "../../Base_url";
export default function Cart(props) {
  const [cart, setCart] = useState([]);
  const [loading, setLoading] = useState(false);
  const [quantity, setQuantity] = useState(1);
  const history = useHistory;
  if (!localStorage.getItem("auth_token")) {
    swal({
      title: "Error",
      text: "Please log in first",
      icon: "error",
    });
    history.push("/");
  }
  //   handle increment decrement option
  const handleIncreament = () => {
    setQuantity((prevCount) => prevCount + 1);
  };

  const handleDecreament = () => {
    if (quantity > 1) {
      setQuantity((prevCount) => prevCount - 1);
    }
  };

  //   handle add to cart
  const handleAddTOCart = (e) => {
    e.preventDefault();
    // const data = {
    //   product_id: product.id,
    //   product_qty: quantity,
    // };

    axios.post("api/add-to-cart", data).then((res) => {
      if (res.data.status == 201) {
        swal({
          title: "Success",
          text: res.data.message,
          icon: "success",
        });
      } else if (res.data.status == 409) {
        // already product added successfully
        swal({
          title: "success",
          text: res.data.message,
          icon: "success",
        });
      } else if (res.data.status == 401) {
        swal({
          title: "error",
          text: res.data.message,
          icon: "error",
        });
      } else if (res.data.status == 404) {
        swal({
          title: "Warning",
          text: res.data.message,
          icon: "warning",
        });
      }
      //   console.log("response from server", res);
    });
  };

  useEffect(() => {
    let isMounted = true;
    if (isMounted) {
      axios.get(`api/cart-data`).then((res) => {
        if (res.data.status == 200) {
          setProduct(res.data.cart);
          setLoading(false);
        } else if (res.data.status == 401) {
          swal({
            title: "Error",
            text: res.data.message,
            icon: "error",
          });
          history.push("/");
        }
      });
    }

    return () => {
      isMounted = false;
    };
  }, [history]);

  // let availableStock = "";
  // if (loading) {
  //   return (
  //     <div className="col-lg-12 mb-4">
  //       <div className="card">
  //         <div className="card-body">
  //           <h4>Cart details loading...</h4>
  //         </div>
  //       </div>
  //     </div>
  //   );
  // }

  return (
    <>
      <div className="container mt-5">
        <nav aria-label="breadcrumb" className="bg-warning py-3 px-4">
          <ol className="breadcrumb">
            <li
              className="breadcrumb-item active text-white display-5"
              aria-current="page"
            >
              Collection /Cart
            </li>
          </ol>
        </nav>

        <div className="card-body">
          <table className="table table-bordered table-striped">
            <thead>
              <tr>
                <th>Image</th>
                <th>Product</th>
                <th>Price</th>
                <th>Quantity</th>
                <th>Total Price</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>
                  <img src="" width={50} height={50} />
                </td>
                <td>Prodcut name</td>
                <td>4000</td>
                <td>
                  <div className="input-group d-flex">
                    <button
                      type="button"
                      className="input-group-text"
                      onClick={() => handleDecreament()}
                    >
                      -
                    </button>
                    <span type="text" className="form-control text-center">
                      {quantity}
                    </span>
                    <button
                      type="button"
                      className="input-group-text"
                      onClick={() => handleIncreament()}
                    >
                      +
                    </button>
                  </div>
                </td>
                <td>11000</td>
                <td>
                  <button className="btn-sm btn-danger" type="button">
                    remove
                  </button>
                </td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    </>
  );
}
