import axios from "axios";
import React, { useEffect, useState } from "react";

export default function Collection() {
  const [category, setCategory] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get("api/get-category-collection").then((res) => {
      if (res.data.status == 200) {
        setCategory(res.data.category);
        setLoading(false);
      }
    });
  }, []);

  if (loading) {
    <h2>loading collection data...</h2>;
  } else {
    let viewcategoryList = "";

    viewcategoryList = category.map((item, index) => {
      return (
        <div className="col-lg-5 card" key={index}>
          <div className="card-body">
            <h4>{item.name}</h4>
          </div>
        </div>
      );
    });
  }

  return (
    <>
      <div className="container mt-5">
        <nav aria-label="breadcrumb" className="bg-warning py-3 px-4">
          <ol class="breadcrumb">
            <li
              class="breadcrumb-item active text-white display-5"
              aria-current="page"
            >
              Home
            </li>
          </ol>
        </nav>

        <div className="row">{view_category_HTML}</div>
      </div>
    </>
  );
}
